import React from "react";

const LibrarySong = ({
  songs,
  song,
  setCurrentSong,
  id,
  audioRef,
  isPlaying,
}) => {
  const songSelectHandler = () => {
    setCurrentSong(song); //asi cambiamos el estado a la cancion que fue clicada
    //add active state
    const newSongs = songs.map((song) => {
      if (song.id === id) {
        return {
          //devuelve la cancion entera pero cambia el active a true
          ...song,
          active: true,
        };
      }
      return {
        //devuelve la cancion entera pero cambia el active a false
        ...song,
        active: false,
      };
    });
    //definimos el state para el array que hemos creado con el map
    setSongs(newSongs);
    //check if the song is playing
    if (isPlaying) {
      const playPromise = audioRef.current.play();
      //si el audio aun no se haya cargado (es undefined), espera un poquiro
      if (playPromise !== undefined) {
        //cuando se cargue la cancion se da play, le damos play
        playPromise.then((audio) => {
          audioRef.current.play();
        });
      }
    }
  };
  //si la cancion esta active se a単ade la clase selected y sino no se a単ade nada
  return (
    <div
      onClick={songSelectHandler}
      className={`library-song ${song.active ? "selected" : ""}`}
    >
      <img alt={song.name} src={song.cover}></img>
      <div className="song-description">
        <h3>{song.name}</h3>
        <h4>{song.artist}</h4>
      </div>
    </div>
  );
};

export default LibrarySong;
