import React, { useRef, useState } from "react";
import { FontAwesome, FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlay,
  faAngleLeft,
  faAngleRight,
  faPause,
} from "@fortawesome/free-solid-svg-icons";

const Player = ({ currentSong, isPlaying, setIsPlaying }) => {
  //state
  const [songInfo, setSongInfo] = useState({
    currentTime: null,
    duration: null,
  });
  const [icon, setIcon] = useState(faPlay);
  //referencias
  const audioRef = useRef(null); //audio de la cancion
  //events
  const playSongHandler = () => {
    //si esta sonando y hacen click al boton, se hace pause, sino se toca la cancion
    if (isPlaying) {
      audioRef.current.pause();
      setIsPlaying(!isPlaying);
      iconUpdateHandler();
    } else {
      audioRef.current.play();
      setIsPlaying(!isPlaying); //cambia el valor al opuesto de lo que estaba
    }
  };
  const timeUpdateHandler = (e) => {
    const current = e.target.currentTime;
    const duration = e.target.duration;
    setSongInfo({ ...songInfo, currentTime: current, duration });
  };

  const iconUpdateHandler = () => {
    setIcon(faPause);
  };

  //otra manera de hacer esa funcion
  // const timeUpdateHandler = ({ target : {currentTime, duration} }) => {
  //   setSongInfo({ ...songInfo, currentTime, duration });
  // };
  const getTime = (time) => {
    return (
      //conseguimos los minutos y los segundos despues
      Math.floor(time / 60) + ":" + ("0" + Math.floor(time % 60)).slice(-2) //slice es una funcion que formata bien el numero
    );
  };

  return (
    <div className="player">
      <div className="time-control">
        <p>{getTime(songInfo.currentTime)}</p>
        <input
          min={0}
          max={songInfo.duration}
          value={songInfo.currentTime}
          type="range"
        />
        <p>{getTime(songInfo.duration)}</p>
      </div>
      <div className="play-control">
        <FontAwesomeIcon className="skip-back" size="2x" icon={faAngleLeft} />
        <FontAwesomeIcon
          onClick={playSongHandler}
          className="play"
          size="2x"
          icon={icon}
        />
        <FontAwesomeIcon
          className="skip-forward"
          size="2x"
          icon={faAngleRight}
        />
      </div>
      <audio
        onTimeUpdate={timeUpdateHandler}
        //cuando se carga la informacion del audio ya empieza
        onLoadedMetadata={timeUpdateHandler}
        ref={audioRef}
        src={currentSong.audio}
      ></audio>
    </div>
  );
};

export default Player;
