//export especificos para funciones y despues es solo importarlas con el nombre de la funcion
export const playAudio = ({ isPlaying, audioRef }) => {
  //check if the song is playing
  if (isPlaying) {
    const playPromise = audioRef.current.play();
    //si el audio aun no se haya cargado (es undefined), espera un poquiro
    if (playPromise !== undefined) {
      //cuando se cargue la cancion se da play, le damos play
      playPromise.then((audio) => {
        audioRef.current.play();
      });
    }
  }
};
